import{_ as a,c as i,o,ag as t}from"./chunks/framework.CQuhCYrb.js";const c=JSON.parse('{"title":"Troubleshooting","description":"","frontmatter":{},"headers":[],"relativePath":"guide/troubleshooting.md","filePath":"guide/troubleshooting.md","lastUpdated":1765440571000}'),s={name:"guide/troubleshooting.md"};function l(r,e,n,h,p,d){return o(),i("div",null,[...e[0]||(e[0]=[t(`<h1 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h1><h2 id="common-issues" tabindex="-1">Common Issues <a class="header-anchor" href="#common-issues" aria-label="Permalink to &quot;Common Issues&quot;">​</a></h2><h3 id="application-won-t-start" tabindex="-1">Application Won&#39;t Start <a class="header-anchor" href="#application-won-t-start" aria-label="Permalink to &quot;Application Won&#39;t Start&quot;">​</a></h3><p><strong>Symptoms</strong>: Nicasa fails to launch or crashes immediately</p><p><strong>Solutions</strong>:</p><ol><li>Check system requirements (see <a href="/guide/introduction.html">Introduction</a>)</li><li>Update your graphics drivers</li><li>Try running as administrator/root</li><li>Reinstall the application</li><li>Check for conflicting software</li></ol><h3 id="images-not-loading" tabindex="-1">Images Not Loading <a class="header-anchor" href="#images-not-loading" aria-label="Permalink to &quot;Images Not Loading&quot;">​</a></h3><p><strong>Symptoms</strong>: Thumbnails don&#39;t appear or images fail to open</p><p><strong>Solutions</strong>:</p><ol><li>Verify the image files aren&#39;t corrupted</li><li>Check file permissions</li><li>Ensure the format is supported (see <a href="/guide/usage.html">Usage</a>)</li><li>Try opening the folder again</li><li>Clear application cache (Settings &gt; Advanced &gt; Clear Cache)</li></ol><h3 id="slow-performance" tabindex="-1">Slow Performance <a class="header-anchor" href="#slow-performance" aria-label="Permalink to &quot;Slow Performance&quot;">​</a></h3><p><strong>Symptoms</strong>: Application is sluggish or thumbnails load slowly</p><p><strong>Solutions</strong>:</p><ol><li>Reduce thumbnail size in settings</li><li>Close other memory-intensive applications</li><li>Ensure adequate free disk space</li><li>Update to the latest version</li><li>Disable animations in settings</li></ol><h3 id="display-issues" tabindex="-1">Display Issues <a class="header-anchor" href="#display-issues" aria-label="Permalink to &quot;Display Issues&quot;">​</a></h3><p><strong>Symptoms</strong>: Interface appears corrupted or text is unreadable</p><p><strong>Solutions</strong>:</p><ol><li>Adjust display scaling in system settings</li><li>Update graphics drivers</li><li>Try different theme options</li><li>Reset to default settings</li></ol><h2 id="error-messages" tabindex="-1">Error Messages <a class="header-anchor" href="#error-messages" aria-label="Permalink to &quot;Error Messages&quot;">​</a></h2><h3 id="file-not-found" tabindex="-1">&quot;File not found&quot; <a class="header-anchor" href="#file-not-found" aria-label="Permalink to &quot;&quot;File not found&quot;&quot;">​</a></h3><p>This usually means the image file has been moved or deleted. Try reopening the folder or selecting a different directory.</p><h3 id="unsupported-format" tabindex="-1">&quot;Unsupported format&quot; <a class="header-anchor" href="#unsupported-format" aria-label="Permalink to &quot;&quot;Unsupported format&quot;&quot;">​</a></h3><p>The image format isn&#39;t recognized. Check our list of <a href="/guide/usage.html">supported formats</a> or convert the image to a compatible format.</p><h3 id="out-of-memory" tabindex="-1">&quot;Out of memory&quot; <a class="header-anchor" href="#out-of-memory" aria-label="Permalink to &quot;&quot;Out of memory&quot;&quot;">​</a></h3><p>Your system doesn&#39;t have enough RAM. Close other applications or upgrade your memory.</p><h2 id="advanced-troubleshooting" tabindex="-1">Advanced Troubleshooting <a class="header-anchor" href="#advanced-troubleshooting" aria-label="Permalink to &quot;Advanced Troubleshooting&quot;">​</a></h2><h3 id="view-logs" tabindex="-1">View Logs <a class="header-anchor" href="#view-logs" aria-label="Permalink to &quot;View Logs&quot;">​</a></h3><p>To access application logs:</p><ol><li>Go to Help &gt; Show Logs</li><li>Or find logs in: <ul><li>Windows: <code>%APPDATA%\\Nicasa\\logs</code></li><li>macOS: <code>~/Library/Application Support/Nicasa/logs</code></li><li>Linux: <code>~/.config/Nicasa/logs</code></li></ul></li></ol><h3 id="reset-application" tabindex="-1">Reset Application <a class="header-anchor" href="#reset-application" aria-label="Permalink to &quot;Reset Application&quot;">​</a></h3><p>To reset all settings to default:</p><ol><li>Close Nicasa</li><li>Delete the application data folder</li><li>Restart Nicasa</li></ol><h3 id="command-line-options" tabindex="-1">Command Line Options <a class="header-anchor" href="#command-line-options" aria-label="Permalink to &quot;Command Line Options&quot;">​</a></h3><p>For advanced users, Nicasa supports command line arguments:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">nicasa</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --verbose</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">          # Enable verbose logging</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">nicasa</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --reset-settings</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">   # Reset all settings</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">nicasa</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /path/to/folder</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">    # Open specific folder</span></span></code></pre></div><h2 id="getting-more-help" tabindex="-1">Getting More Help <a class="header-anchor" href="#getting-more-help" aria-label="Permalink to &quot;Getting More Help&quot;">​</a></h2><p>If these solutions don&#39;t resolve your issue:</p><ol><li>Check our <a href="/guide/faq.html">FAQ</a></li><li>Search existing <a href="https://github.com/nicasa-project/nicasa-support/issues" target="_blank" rel="noreferrer">issues</a></li><li>Create a new issue with: <ul><li>Your operating system and version</li><li>Nicasa version</li><li>Steps to reproduce the problem</li><li>Any error messages or screenshots</li></ul></li></ol>`,38)])])}const g=a(s,[["render",l]]);export{c as __pageData,g as default};
